mapper.update:
UPDATE POLITICIAN
SET LIKES = LIKES+1
WHERE ID = #{politicianNum}

UPDATE P_LIKES
SET LIKE_STATE = 0
WHERE MEMBER_ID = #{memberId} AND POLITICIAN_NUM = #{politicianNum}

UPDATE POLITICIAN
SET LIKES = LIKES-1
WHERE ID = #{politicianNum}

mapper.insert:
UPDATE POLITICIAN
SET DISLIKE = DISLIKE+1
WHERE ID = #{politicianNum}

mapper.update:
UPDATE POLITICIAN
SET DISLIKE = DISLIKE-1
WHERE ID = #{politicianNum}

mapper.select:
SELECT COUNT(*)
FROM P_LIKES
WHERE MEMBER_ID = #{memberId} AND POLITICIAN_NUM = #{politicianNum}

SELECT LIKE_STATE
FROM P_LIKES
WHERE MEMBER_ID = #{memberId} AND POLITICIAN_NUM = #{politicianNum}
