mapper.update:
UPDATE POLITICIAN
SET BIRTH = #{birth}, GRADE = #{grade}
WHERE ID = #{id}

UPDATE POLITICIAN
SET LIKES = LIKES+1
WHERE ID = #{id}

UPDATE POLITICIAN
SET DISLIKE = DISLIKE+1
WHERE ID = #{id}

mapper.select:
SELECT ID, NAME, KOR_SCORE, ENG_SCORE, MATH_SCORE
FROM POLITICIAN

    WHERE ${searchCode} = #{keyword}
